import React from "react";
import connectDB from "@/lib/db";
import User from "@/models/User";
import NGOCard from "@/features/donor/components/NgoCards";
import SearchNGOs from "@/features/donor/components/SearchNGOs";
import { getServerSession } from "next-auth";
import { authOptions } from "@/app/api/auth/[...nextauth]/route";
import { Plus, Inbox } from "lucide-react";
import Link from "next/link";

export default async function DonorDashboard({ searchParams }) {
  const session = await getServerSession(authOptions);
  const userEmail = session?.user?.email || "Guest";
  const initial = userEmail.charAt(0).toUpperCase();
  
  await connectDB();

  // Extract search query from URL
  const query = (await searchParams).query || "";

  // Build MongoDB Filter
  const filter = {
    role: "ngo",
    status: "approved",
  };

  if (query) {
    filter.$or = [
      { "ngoDetails.ngoName": { $regex: query, $options: "i" } },
      { "ngoDetails.address": { $regex: query, $options: "i" } },
      { "ngoDetails.ngoType": { $regex: query, $options: "i" } },
    ];
  }

  const ngos = await User.find(filter).sort({ createdAt: -1 }).lean();

  const groupedNgos = ngos.reduce((acc, ngo) => {
    const category = ngo.ngoDetails?.ngoType || "Other";
    if (!acc[category]) acc[category] = [];
    acc[category].push(ngo);
    return acc;
  }, {});

  return (
    <div className="min-h-screen bg-[#FDFDFD] text-slate-900 selection:bg-emerald-100">
      <nav className="sticky top-0 z-30 bg-white/80 backdrop-blur-md border-b border-slate-100">
        <div className="max-w-7xl mx-auto px-6 h-16 flex items-center justify-between">
          <div className="flex items-center gap-2.5">
            <div className="w-8 h-8 bg-emerald-600 rounded-lg flex items-center justify-center shadow-sm shadow-emerald-200">
              <Plus size={18} className="text-white" />
            </div>
            <span className="font-bold tracking-tight text-lg">FOODLINK</span>
          </div>

          <div className="flex items-center gap-6">
            <SearchNGOs />

            {/* Dynamic Profile Bubble */}
            <div 
              title={userEmail}
              className="h-9 w-9 bg-emerald-50 border border-emerald-100 rounded-full flex items-center justify-center shadow-sm hover:scale-105 transition-transform cursor-pointer"
            >
              <span className="text-emerald-700 font-bold text-sm">
                {initial}
              </span>
            </div>
          </div>
        </div>
      </nav>

      <main className="max-w-7xl mx-auto px-6 py-10">
        <header className="mb-12">
          <h1 className="text-3xl font-extrabold tracking-tight text-slate-900">
            {query ? `Results for "${query}"` : "Verified Organizations"}
          </h1>
          <p className="text-slate-500 mt-2 text-sm font-medium">
            Showing {ngos.length} organization{ngos.length !== 1 ? 's' : ''} available for donation.
          </p>
        </header>

        {Object.keys(groupedNgos).length > 0 ? (
          Object.keys(groupedNgos).map((category) => (
            <section key={category} className="mb-16">
              <div className="flex items-center gap-4 mb-8">
                <h2 className="text-xs font-bold text-emerald-600 uppercase tracking-[0.2em]">
                  {category}
                </h2>
                <div className="h-px flex-1 bg-slate-100"></div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-x-8 gap-y-10">
                {groupedNgos[category].map((ngo) => (
                  <NGOCard key={ngo._id.toString()} ngo={ngo} />
                ))}
              </div>
            </section>
          ))
        ) : (
          <div className="h-80 flex flex-col items-center justify-center border border-dashed border-slate-200 rounded-3xl bg-slate-50/30">
            <div className="p-4 bg-white rounded-2xl shadow-sm mb-4">
              <Inbox className="text-slate-300" size={32} />
            </div>
            <p className="text-slate-500 font-medium">
              No organizations found matching &quot;{query}&quot;
            </p>
            <Link 
              href="/donor" 
              className="mt-4 text-xs font-bold text-emerald-600 uppercase tracking-widest hover:text-emerald-700"
            >
              Clear all filters
            </Link>
          </div>
        )}
      </main>
    </div>
  );
}

