"use server";

import connectDB from "@/lib/db";
import Listing from "@/models/Listing";
import { getServerSession } from "next-auth";
import { authOptions } from "@/app/api/auth/[...nextauth]/route";
import { revalidatePath } from "next/cache";

export async function createListing(prevState, formData) {
  // 1. Authenticate the User
  const session = await getServerSession(authOptions);
  
  if (!session || session.user.role !== "donor") {
    return { error: "Unauthorized. Only donors can post food." };
  }

  await connectDB();

  // 2. Extract data from form
  const title = formData.get("title");
  const description = formData.get("description");
  const foodType = formData.get("foodType");
  const quantity = formData.get("quantity");
  const pickupAddress = formData.get("pickupAddress");
  const expiryTime = formData.get("expiryTime");
  const cookedTime = formData.get("cookedTime");

  // 3. Simple Validation
  if (!title || !description || !expiryTime) {
    return { error: "Please fill in all required fields." };
  }

  try {
    // 4. Create the Listing
    const newListing = await Listing.create({
      donorId: session.user.id,
      title,
      description,
      foodType,
      quantity,
      pickupAddress,
      expiryTime: new Date(expiryTime),
      cookedTime: cookedTime ? new Date(cookedTime) : null,
      status: "available",
    });

    // 5. Refresh the page data so the new listing shows up
    revalidatePath("/donor/dashboard"); 
    
    return { success: "Donation posted successfully!", data: JSON.parse(JSON.stringify(newListing)) };
  } catch (error) {
    console.error("LISTING_CREATION_ERROR:", error);
    return { error: "Internal Server Error. Please try again." };
  }
}

